mapper.insert:
		insert into
		riunione(id_palazzina, id_votazione, data, motivazione,
		n_verbale)
		values (#{id_palazzina}
		, #{id_votazione}
		, #{data}
		, #{motivazione}
		,
		#{n_verbale}
		)

mapper.update:
		update riunione set
		id_palazzina=#{id_palazzina}
		,id_votazione= #{id_votazione}
		,data=#{data}
		,motivazione=#{motivazione}
		,n_verbale=#{n_verbale}
		where
		id = #{id}
